import socket
import time

# 目标主机的IP和UDP端口
target_host = "ts.populargamers.co.za"
target_port = 6969

# 创建一个UDP socket
client = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
# 设置等待超时为5秒
client.settimeout(5.0)

# 定义要发送的十六进制数据包列表
hex_data_packets = [
    #连接
    '000004172710198000000000334A0146',
    #开始
    '',
    #更新
    ''
]

# 使用循环来连续发送和接收
while True:
    for hex_data in hex_data_packets:
        byte_data = bytes.fromhex(hex_data)
        try:
            # 发送十六进制数据
            client.sendto(byte_data, (target_host, target_port))
            # 开始时间
            start_time = time.time()
            # 试图接收响应
            data, addr = client.recvfrom(4096)
            # 结束时间
            end_time = time.time()
            # 计算延迟
            delay = end_time - start_time
            print(f"Received response from {addr} with delay of {delay} seconds")
        except socket.timeout:
            print("UDP request timed out")
        except Exception as e:
            print(f"An error occurred: {e}")
            
        # 指定每次发送后的等待时间，例如1秒
        sleep_time = 1
        time.sleep(sleep_time)

